import React, { useState } from 'react';
import './App.css';
import InstructionForm from './components/InstructionForm';  // Import InstructionForm component

const App = () => {
  const [generatedAnswer, setGeneratedAnswer] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleGenerateAnswer = (answer) => {
    setGeneratedAnswer(answer);
  };

  const handleError = (error) => {
    setError(error);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Instruction-based Answer Generator</h1>
      </header>

      {/* Pass props down to InstructionForm */}
      <InstructionForm
        onGenerateAnswer={handleGenerateAnswer}
        onError={handleError}
        loading={loading}
        setLoading={setLoading}
      />

      {/* Display the generated answer */}
      {error && <div className="error">{error}</div>}
      {generatedAnswer && (
        <div className="generated-answer">
          <h2>Generated Answer:</h2>
          <p>{generatedAnswer}</p>
        </div>
      )}
    </div>
  );
};

export default App;
