import React, { useState } from "react";
import "./quizzes.css";
import "../App.css";
import CodeHelper from "../components/codeHelper";

const dummyQuestions = [
  {
    id: 1,
    question:
      "What is the correct syntax to output 'Hello World' in JavaScript?",
    choices: [
      "print('Hello World')",
      "console.log('Hello World')",
      "echo 'Hello World'",
      "printf('Hello World')",
    ],
  },
  {
    id: 2,
    question: "Which company developed JavaScript?",
    choices: ["Netscape", "Microsoft", "Google", "Oracle"],
  },
];

const JavaScriptQuizzesPage = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  const currentQuestion = dummyQuestions[currentQuestionIndex];

  const handleChoiceClick = (choice) => {
    console.log("Selected choice:", choice);
    // Later you can save the answer here
  };

  return (
    <div className="app-layout">
      <header className="main-header">
        <h1>Code Learning Platform</h1>
        <nav className="main-menu">
          <a href="#lessons">Lessons</a>
          <a href="#quizzes">Quizzes</a>
          <a href="#instructions">Code Generator</a>
        </nav>
      </header>

      <div className="quiz-page">
        <div className="quiz-left">
          {dummyQuestions.length > 0 ? (
            dummyQuestions.map((questionItem, index) => (
              <div className="question-card" key={questionItem.id}>
                <div className="question-number">Question {index + 1}</div>
                <div className="question-text">{questionItem.question}</div>
                <div className="choices-list">
                  {questionItem.choices.map((choice, choiceIndex) => (
                    <button
                      key={choiceIndex}
                      className="choice-button"
                      onClick={() => handleChoiceClick(choice)}
                    >
                      {choice}
                    </button>
                  ))}
                </div>
              </div>
            ))
          ) : (
            <p>No questions found.</p>
          )}
        </div>

        <div className="quiz-right">
          <div className="empty-card">
            <CodeHelper />
          </div>
        </div>
      </div>
    </div>
  );
};

export default JavaScriptQuizzesPage;
