import React from "react";
import "../App.css"; // Ensure your styles are applied correctly

const levels = [
  { name: "Easy", emoji: "游릭", description: "Perfect for beginners." },
  { name: "Intermediate", emoji: "游리", description: "Some experience required." },
  { name: "Hard", emoji: "游댮", description: "Challenging for advanced learners." },
];

const LevelSelect = ({ onSelect }) => {
  return (
    <div className="app-layout">
      <header className="main-header">
        <h1>Select Difficulty Level</h1>
      </header>

      <div className="main-content quiz-grid">
        {levels.map((level) => (
          <section
            key={level.name}
            className="section-card enhanced-card"
            onClick={() => onSelect(level.name)}
            style={{ cursor: "pointer" }}
          >
            <h2>
              {level.emoji} {level.name}
            </h2>
            <p>{level.description}</p>
            <button>Select {level.name}</button>
          </section>
        ))}
      </div>
    </div>
  );
};

export default LevelSelect;
