import React from "react";

const LanguageSelect = ({ onSelect }) => {
  const languages = [
    { name: "Python", emoji: "üêç" },
    { name: "JavaScript", emoji: "üìú" },
  ];

  return (
    <div className="app-layout">
        <section className="hero-section">
        <div className="hero-text">
          <h1>Select Programming Language</h1>
          <p>Master Python and JavaScript with interactive lessons, quick quizzes, and AI assistance.</p>
          <div className="hero-buttons">
            <Link to="/lessons" className="cta-btn primary">üìò Start Learning</Link>
            <Link to="/quizzes" className="cta-btn secondary">üß† Take a Quiz</Link>
          </div>
        </div>
      </section>


      <div className="main-content quiz-grid">
        {languages.map((lang) => (
          <section
            key={lang.name}
            className="section-card enhanced-card"
            onClick={() => onSelect(lang.name)}
            style={{ cursor: "pointer" }}
          >
            <h2>
              {lang.emoji} {lang.name}
            </h2>
            <p>Start quizzes for {lang.name}</p>
            <button>Select</button>
          </section>
        ))}
      </div>
    </div>
  );
};

export default LanguageSelect;
