import React, { useEffect, useState } from "react";
import { fetchQuizzesFromAPI } from "../../services/api"; // your API logic

const QuizSession = ({ language, difficulty, onComplete }) => {
  const [questions, setQuestions] = useState([]);
  const [current, setCurrent] = useState(0);
  const [score, setScore] = useState(0);

  useEffect(() => {
    const load = async () => {
      const data = await fetchQuizzesFromAPI(); // Fetch 5
      setQuestions(data);
    };
    load();
  }, [language, difficulty]);

  const handleAnswer = (answer) => {
    if (answer === questions[current].correct_answer) {
      setScore((prev) => prev + 1);
    }
    if (current + 1 < questions.length) {
      setCurrent((prev) => prev + 1);
    } else {
      onComplete(questions, score + (answer === questions[current].correct_answer ? 1 : 0));
    }
  };

  if (!questions.length) return <p>Loading questions...</p>;

  const q = questions[current];
  console.log("sdd ",score)
  return (
    <div className="quiz-box">
      <h3>Question {current + 1} of {questions.length}</h3>
      <p>{q.question}</p>
      {/* Assuming options could be extended later */}
      {q.answer_options.map((option, index) => {
  const label = String.fromCharCode(65 + index); // Converts 0 -> 'A', 1 -> 'B', etc.
  return (
    <button key={index} onClick={() => handleAnswer(option)}>
      {label}. {option}
    </button>
  );
})}
    </div>
  );
};

export default QuizSession;
