import React from 'react';
import './App.css';
import InstructionForm from './components/codeHelper';

const App = () => {
  return (
    <div className="app-container">
      <header className="main-header">
        <h1>Code Learning Platform</h1>
        <nav className="main-menu">
          <a href="#lessons">Lessons</a>
          <a href="#quizzes">Quizzes</a>
          <a href="#instructions">Instruction Form</a>
        </nav>
      </header>

      <main className="content">
        <section id="lessons" className="section-card">
          <h2>Lessons</h2>
          <p>Begin your coding journey with step-by-step lessons covering Python basics, variables, loops, and more!</p>
        </section>

        <section id="quizzes" className="section-card">
          <h2>Quizzes</h2>
          <p>Test your knowledge with interactive quizzes after each lesson.</p>
        </section>

        <section id="instructions" className="section-card">
          <InstructionForm />
        </section>
      </main>
    </div>
  );
};

export default App;
