import React, { useState } from 'react';
import { generateAnswer } from '../services/api';

const InstructionForm = () => {
  const [instruction, setInstruction] = useState('');
  const [generatedAnswer, setGeneratedAnswer] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    try {
      const answer = await generateAnswer(instruction);
      setGeneratedAnswer(answer);
    } catch (err) {
      setError('Failed to generate answer');
    }
    setLoading(false);
  };

  return (
    <div>
      <h2>Instruction Generator</h2>
      <form onSubmit={handleSubmit}>
        <textarea
          value={instruction}
          onChange={(e) => setInstruction(e.target.value)}
          placeholder="Enter instruction here"
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Generating...' : 'Generate Answer'}
        </button>
      </form>
      {error && <div style={{ color: 'red' }}>{error}</div>}
      {generatedAnswer && <div><h3>Generated Answer:</h3><p>{generatedAnswer}</p></div>}
    </div>
  );
};

export default InstructionForm;
