import React, { useState } from 'react';
import { generateAnswer } from '../../services/api';
import { EventEmitter } from 'events';
import './codeHelper_styles.css'


export const codeHelperEvents = new EventEmitter();

const CodeHelper = () => {
  const [instruction, setInstruction] = useState('');
  const [generatedAnswer, setGeneratedAnswer] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [questionHistory, setQuestionHistory] = useState([]);
  const [showRecommendationPrompt, setShowRecommendationPrompt] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!instruction.trim()) return;

    setLoading(true);
    setError('');
    try {
      const answer = await generateAnswer(instruction);
      setGeneratedAnswer(answer);
      const newHistory = [...questionHistory, instruction].slice(-2);
      setQuestionHistory(newHistory);

      if (newHistory.length >= 2 && !showRecommendationPrompt) {
        setShowRecommendationPrompt(true);
      }

    } catch (err) {
      setError('⚠️ Failed to generate answer. Try again later.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleGetRecommendations = () => {
    if (questionHistory.length >= 2) {
      codeHelperEvents.emit('newInstructions', questionHistory.join(' '));
      setShowRecommendationPrompt(false);
    }
  };

  return (
    <div className="app-layout">
      <header className="main-header">
        <h1>💡 Code Helper Assistant</h1>
        <p>Get quick code answers for Python, JavaScript, and more.</p>
      </header>

      <div className="main-content helper-container enhanced-card">
        <form onSubmit={handleSubmit}>
          <label htmlFor="instruction" className="input-label">
            Enter your instruction:
          </label>
          <textarea
            id="instruction"
            className="input-textarea"
            value={instruction}
            onChange={(e) => setInstruction(e.target.value)}
            placeholder="e.g., Write a Python function to reverse a string"
            rows={5}
          />

          <div className="form-actions">
            <button
              type="submit"
              disabled={loading || !instruction.trim()}
              className={`primary-btn ${loading ? 'loading' : ''}`}
            >
              {loading ? (
                <>
                  <span className="spinner"></span> Generating...
                </>
              ) : (
                'Generate Answer'
              )}
            </button>

            {showRecommendationPrompt && (
              <button
                type="button"
                onClick={handleGetRecommendations}
                className="secondary-btn"
              >
                Get Recommendations
              </button>
            )}
          </div>
        </form>

        {error && <div className="error-message">{error}</div>}

        {generatedAnswer && (
          <div className="generated-answer">
            <h3>Generated Answer:</h3>
            <pre className="code-block">{generatedAnswer}</pre>
          </div>
        )}
      </div>
    </div>
  );
};

export default CodeHelper;
