import React from 'react';
import './App.css';
import InstructionForm from './components/codeHelper';

const App = () => {
  return (
    <div className="app-layout">
      <header className="main-header">
        <h1>Code Learning Platform</h1>
      </header>

      <div className="main-content">
        {/* Left Column - Learning Modules */}
        <div className="left-panel">
          <section className="section-card" id="lessons">
            <h2>Lessons</h2>
            <p>Learn Python step by step with beginner-friendly examples covering syntax, variables, loops, and more.</p>
          </section>

          <section className="section-card" id="quizzes">
            <h2>Quizzes</h2>
            <p>Reinforce your understanding with fun, interactive quizzes after each lesson.</p>
          </section>
        </div>

        {/* Right Column - Code Generator */}
        <div className="right-panel">
          <section className="section-card" id="instructions">
            <InstructionForm />
          </section>
        </div>
      </div>
    </div>
  );
};

export default App;
