import React from "react";
import "./home_styles.css";
import { Link } from "react-router-dom";
import CodingChatBot from "../components/chatbot/coding_chatbot";

const Home = () => {
  return (
    <div className="home-page">
      {/* Header / Nav */}
      <header className="main-header">
        <h1 className="brand">ðŸš€ Code Learning Platform</h1>
        <nav className="main-menu">
          <Link to="/">Home</Link>
          <Link to="/quizzes">Quizzes</Link>
          <Link to="/instructions">Code Generator</Link>
        </nav>
      </header>

      {/* Hero Section */}
      <section className="hero">
        <h2>Welcome to Your Interactive Coding Playground</h2>
        <p>Learn Python & JavaScript through guided lessons, smart quizzes, and AI-powered help.</p>
        <Link to="/lessons" className="cta-btn">Start Learning</Link>
      </section>

      {/* Main Content Grid */}
      <main className="home-grid">
        {/* Lessons Section */}
        <section className="section-card enhanced-card" id="lessons">
          <h2>ðŸ“˜ Lessons</h2>
          <ul className="lesson-list">
            <li><strong>Lesson 1:</strong> Variables and Data Types</li>
            <li><strong>Lesson 2:</strong> Conditional Statements</li>
            <li><strong>Lesson 3:</strong> Loops</li>
            <li><strong>Lesson 4:</strong> Functions</li>
            <li><strong>Lesson 5:</strong> Lists and Dictionaries</li>
          </ul>
          <p>Master Python step-by-step with explanations and examples.</p>
        </section>

        {/* Quizzes Section */}
        <Link to="/quizzes" className="link-wrapper">
          <section className="section-card enhanced-card" id="quizzes">
            <h2>ðŸ§  Quizzes</h2>
            <ul className="quiz-list">
              <li>ðŸ’¡ Quiz 1: Python Basics</li>
              <li>ðŸ’¡ Quiz 2: Control Flow</li>
              <li>ðŸ’¡ Quiz 3: Functions & Loops</li>
            </ul>
            <p>Reinforce learning with short quizzes and get instant feedback.</p>
          </section>
        </Link>

      </main>

      {/* Floating Chatbot */}
      <CodingChatBot />
    </div>
  );
};

export default Home;
