import React, { useState } from 'react';
import { generateAnswer } from '../../services/api';
import { EventEmitter } from 'events';
export const codeHelperEvents = new EventEmitter();

const CodeHelper = () => {
  const [instruction, setInstruction] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!instruction.trim()) return;

    const userMessage = { type: 'user', text: instruction };
    setChatHistory((prev) => [...prev, userMessage]);
    setInstruction('');
    setLoading(true);
    setError('');

    try {
      const answer = await generateAnswer(userMessage.text);
      const botMessage = { type: 'bot', text: answer };
      setChatHistory((prev) => [...prev, botMessage]);
    } catch (err) {
      setError('‚ö†Ô∏è Failed to get response.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="chat-container app-layout">
      <header className="main-header">
        <h1>üí¨ Code Assistant</h1>
        <p>Chat with an AI to generate code, get help, or solve problems.</p>
      </header>

      <div className="chat-box">
        {chatHistory.map((msg, idx) => (
          <div
            key={idx}
            className={`chat-message ${msg.type === 'user' ? 'user' : 'bot'}`}
          >
            <div className="message-content">
              {msg.type === 'bot' ? (
                <pre>{msg.text}</pre>
              ) : (
                <p>{msg.text}</p>
              )}
            </div>
          </div>
        ))}
        {loading && (
          <div className="chat-message bot">
            <div className="message-content">
              <p><span className="spinner"></span> Generating...</p>
            </div>
          </div>
        )}
      </div>

      <form className="chat-form" onSubmit={handleSubmit}>
        <textarea
          rows={2}
          placeholder="Ask your coding question here..."
          value={instruction}
          onChange={(e) => setInstruction(e.target.value)}
          className="chat-input"
        />
        <button type="submit" className="send-btn" disabled={loading || !instruction.trim()}>
          Send
        </button>
      </form>

      {error && <div className="error-message">{error}</div>}
    </div>
  );
};

export default CodeHelper;
