import React, { useEffect, useState } from "react";
import "./quizzes.css";
import "../App.css";
import { Link } from "react-router-dom";
import { fetchQuizLevels } from "../services/api";

// QuizSection component for reusable rendering
const QuizSection = ({ title, quizzes, basePath }) => {
  return (
    <div className="quiz-panel">
      <h2>{title}</h2>
      {quizzes.map((item) => (
        <Link
          key={`${item.quiz_type}-${item.level}`}
          to={basePath}
          state={{ level: item.level }}
          className="section-card enhanced-card link-wrapper"
        >
          <h3>
            {item.emoji} {item.title}
          </h3>
          <p><strong>Topic:</strong> {item.topic}</p>
          <p><strong>Level:</strong> {item.level}</p>
          <p><strong>Questions:</strong> 5</p>
          <p><em>{item.description}</em></p>
          <button>Start Quiz</button>
        </Link>
      ))}
    </div>
  );
};

const QuizzesPage = () => {
  const [quizzes, setQuizzes] = useState([]);

  useEffect(() => {
    const loadQuizzes = async () => {
      const data = await fetchQuizLevels();
      setQuizzes(data);
    };

    loadQuizzes();
  }, []);

  const pythonLevels = quizzes.filter((q) => q.quiz_type === "Python");
  const jsLevels = quizzes.filter((q) => q.quiz_type === "JavaScript");

  return (
    <div className="app-layout">
      <header className="main-header">
        <h1>Code Learning Platform</h1>
        <nav className="main-menu">
          <Link to="/">Lessons</Link>
          <Link to="/quizzes">Quizzes</Link>
          <Link to="/instructions">Code Generator</Link>
        </nav>
      </header>

      <div className="main-content quiz-grid">
        <QuizSection title="ðŸ Python Quizzes" quizzes={pythonLevels} basePath="/python-quizzes" />
        <QuizSection title="ðŸ“œ JavaScript Quizzes" quizzes={jsLevels} basePath="/javascript-quizzes" />
      </div>
    </div>
  );
};

export default QuizzesPage;
