import React, { useState } from "react";
import "./quizzes.css";
import "../App.css";
import { useNavigate } from "react-router-dom";

const QuizzesPage = () => {
  const [click, isClick] = useState(false);
  const navigate = useNavigate();

  const handleNavigate = (path) => {
    isClick(true);
    navigate({ path });
  };

  return (
    <div className="app-layout">
      <header className="main-header">
        <h1>Code Learning Platform</h1>
        <nav className="main-menu">
          <a href="#lessons">Lessons</a>
          <a href="#quizzes">Quizzes</a>
          <a href="#instructions">Code Generator</a>
        </nav>
      </header>

      <div className="main-content">
        {/* Left Column */}
        <div
          className="left-panel"
          onClick={() => handleNavigate("/python-quizzes")}
        >
          <section className="section-card enhanced-card" id="lessons">
            <h2>ðŸ“˜ Python Quizzes</h2>
            <ul className="lesson-list">
              <li>ðŸ’¡ Quiz 1: Python Basics</li>
              <li>ðŸ’¡ Quiz 2: Control Flow</li>
              <li>ðŸ’¡ Quiz 3: Functions & Loops</li>
            </ul>
            <p>
              Master Python from the ground up. Each lesson includes examples
              and exercises.
            </p>
          </section>
          <button type="submit" onClick={handleNavigate}>
            Click me
          </button>
          {click ? "Hello World, Button is clicked!!!" : ""}
        </div>

        {/* Right Column */}
        <div
          className="right-panel"
          onClick={() => handleNavigate("/javascript-quizzes")}
        >
          <section className="section-card enhanced-card" id="quizzes">
            <h2>ðŸ“˜ JavaScript Quizzes</h2>
            <ul className="quiz-list">
              <li>ðŸ’¡ Quiz 1: Python Basics</li>
              <li>ðŸ’¡ Quiz 2: Control Flow</li>
              <li>ðŸ’¡ Quiz 3: Functions & Loops</li>
            </ul>
            <p>
              Master JavaScript from the ground up. Each lesson includes
              examples and exercises.
            </p>
          </section>
        </div>
      </div>
    </div>
  );
};

export default QuizzesPage;
